export default {
  data: function () {
    return {
      products: [],
      search:'',
      noMatch:null,
      list: [],
      productsNum:null,
      current:null,
      pages:null,
      page:null
    }
  },
  computed: {
    isAdmin(){
      console.log(this.$store.getters.isAdmin)
      return this.$store.getters.isAdmin;
    },
    changelist() {
      while (this.list.length) {
        this.list.pop();
      } 
      for(var i= (Number(this.current) > 5 ? Number(this.current) - 4 : 1); i <= (Number(this.current) + 4) && i <= this.pages; i++) this.list.push(i);
      console.log(this.list);
      return this.list;
        
    }
  },
  methods: {
    createProduct: function() {
      this.$router.push('/products/new');
    },
    searchProduct: function() {
      this.noMatch = null;
      console.log(this.search);
      this.$http.get('/api/products?search=' + this.search)
      .then((response) => {
          console.log(response.data);
          if(response.data.noMatch) {
            this.noMatch = response.data.noMatch;
          } else {
            this.products = response.data.products;
            this.current = response.data.current;
            this.pages = response.data.pages;
            this.page = response.data.page;
            this.productsNum = response.data.productsNum;
          }
      })
    },

    mipageProduct: function() {
      this.$http.get('/api/products?page=' + (Number(this.current) - 1) + '&search=' + this.search)
      .then((response) => {
          this.products = response.data.products;
          this.current = response.data.current;
          this.pages = response.data.pages;
          this.page = response.data.page;
          this.productsNum = response.data.productsNum;
      })
    },
    numpageProduct: function(i) {
      this.$http.get('/api/products?page=' + i + '&search=' + this.search)
      .then((response) => {
          this.products = response.data.products;
          this.current = response.data.current;
          this.pages = response.data.pages;
          this.page = response.data.page;
          this.productsNum = response.data.productsNum;
      })
    },
    pluspageProduct: function() {
      this.$http.get('/api/products?page=' + (Number(this.current) + 1) + '&search=' + this.search)
      .then((response) => {
          this.products = response.data.products;
          this.current = response.data.current;
          this.pages = response.data.pages;
          this.page = response.data.page;
          this.productsNum = response.data.productsNum;
      })
    }
  },
  created() {
      this.$http.get('/api/products')
      .then((response) => {
          this.products = response.data.products;
          this.current = response.data.current;
          this.pages = response.data.pages;
          this.page = response.data.page;
          this.productsNum = response.data.productsNum;
      })
  }
}
