export default {
  data: function () {
    return {
      products: [],
      list: [],
      productsNum:null,
      current:null,
      pages:null,
      page:null
    }
  },
  computed: {
    isAdmin(){
      return this.$store.getters.isAdmin;
    },
    changelist() {
      while (this.list.length) {
        this.list.pop();
      } 
      if(Number(this.current) < 6) {
        for(var i= (Number(this.current) % 6 == 0 ? Number(this.current) : Number(this.current) - Number(this.current) % 6 + 1 ), j= i;   i <= (j + 4) && i <= this.pages; i++) this.list.push(i);
        return this.list;  
      } else {
        for(var i= (Number(this.current) % 6 == 0 ? Number(this.current) : Number(this.current) - Number(this.current) % 6), j= i; i <= (j + 5) && i <= this.pages; i++) this.list.push(i);
        return this.list; 
      }
    }
  },
  methods: {
    createProduct: function() {
      this.$router.push('/products/new');
    },

    mipageProduct: function() {
      this.$http.get('/api/products/accs?page=' + (Number(this.current) - 1))
      .then((response) => {
          this.products = response.data.products;
          this.current = response.data.current;
          this.pages = response.data.pages;
          this.page = response.data.page;
          this.productsNum = response.data.productsNum;
          localStorage.setItem("accsPage", this.current);
          this.$store.state.accsPage = this.current;
          this.$router.push('/products/accs?page=' + (Number(this.current)));
      })
    },
    numpageProduct: function(i) {
      this.$http.get('/api/products/accs?page=' + i)
      .then((response) => {
          this.products = response.data.products;
          this.current = response.data.current;
          this.pages = response.data.pages;
          this.page = response.data.page;
          this.productsNum = response.data.productsNum;
          localStorage.setItem("accsPage", this.current);
          this.$store.state.accsPage = this.current;
          this.$router.push('/products/accs?page=' + (Number(this.current)));
      })
    },
    pluspageProduct: function() {
      this.$http.get('/api/products/accs?page=' + (Number(this.current) + 1))
      .then((response) => {
          this.products = response.data.products;
          this.current = response.data.current;
          this.pages = response.data.pages;
          this.page = response.data.page;
          this.productsNum = response.data.productsNum;
          localStorage.setItem("accsPage", this.current);
          this.$store.state.accsPage = this.current;
          this.$router.push('/products/accs?page=' + (Number(this.current)));
      })
    }
  },
  created() {
      this.current = this.$store.getters.accsPage;
      this.$http.get('/api/products/accs?page=' + (Number(this.current)))
      .then((response) => {
          this.products = response.data.products;
          this.current = response.data.current;
          this.pages = response.data.pages;
          this.page = response.data.page;
          this.productsNum = response.data.productsNum;
      })
  }
}
